import React, { useEffect, useState } from "react";
import { Navbar } from "react-bootstrap";
import "./style.css";
import { useNavigate } from "react-router-dom";

const NavigationBar = () => {
  const navigate = useNavigate();
  const locations = ["Delhi", "Mumbai", "Bangalore", "Chennai"];

  const [userRole, setUserRole] = useState("guest");
  const [userName, setUserName] = useState("");

  useEffect(() => {
    const updateUserInfo = () => {
      const storedRole = localStorage.getItem("role");
      const storedName = localStorage.getItem("name");

      if (storedRole) {
        setUserRole(storedRole);
        if (storedRole === "professional" || storedRole === "user") {
          setUserName(storedName || "User");
        }
      } else {
        setUserRole("guest");
        setUserName("");
      }
    };

    // Run on first render
    updateUserInfo();

    // ðŸ”„ Listen for changes from other components
    window.addEventListener("storage", updateUserInfo);
    window.addEventListener("userUpdated", updateUserInfo);

    return () => {
      window.removeEventListener("storage", updateUserInfo);
      window.removeEventListener("userUpdated", updateUserInfo);
    };
  }, []);

  const getDisplayName = () => {
    if (userRole === "guest") return "Guest";
    if (userRole === "admin") return "Admin";
    if (userRole === "professional") return userName || "Professional";
    if (userRole === "user") return userName || "User";
  };

  const handleAuthClick = () => {
    if (userRole === "guest") {
      navigate("/login");
    } else {
      // Clear localStorage on logout
      localStorage.removeItem("role");
      localStorage.removeItem("name");

      // ðŸ”¥ Notify other components about logout
      window.dispatchEvent(new Event("userUpdated"));

      setUserRole("guest");
      setUserName("");
      navigate("/");
    }
  };

  return (
    <nav className="navbar nav-fix border-bottom navbar-expand-lg navbar-light fixed-top">
      <div className="container-fluid">
        <Navbar.Brand onClick={() => navigate("/")}>
          <img
            alt="logo"
            src="https://drive.google.com/thumbnail?id=1poFTLph9EAz5v-J8wPB-PzuKa30-F0mb&sz=w1000"
            width="35"
            height="35"
            className="d-inline-block align-top rounded-circle"
          />{" "}
          BookMyClean
        </Navbar.Brand>

        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div
          className="collapse navbar-collapse m-2"
          id="navbarSupportedContent"
        >
          {/* Location Dropdown */}
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <select className="form-select ms-2" aria-label="Select location">
                <option value="" disabled selected>
                  Select Location
                </option>
                {locations.map((loc, index) => (
                  <option key={index} value={loc}>
                    {loc}
                  </option>
                ))}
              </select>
            </li>
          </ul>

          {/* User Role/Name Display */}
          <div className="me-3 fw-semibold text-primary">
            {getDisplayName()}
          </div>

          {/* Login / Logout Button */}
          <button
            className="btn checkout-btn"
            style={{ width: "100px" }}
            onClick={handleAuthClick}
          >
            {userRole === "guest" ? "Login" : "Logout"}
          </button>
        </div>
      </div>
    </nav>
  );
};

export default NavigationBar;
