import React, { Component } from "react";
import "./style.css";
import apartment from "../images/apartment.jpeg";

const services = [
  {
    heading: "Apartment",
    types: [
      {
        title: "Furnished apartment",
        price: 3499,
        time: "3 hrs 45 mins",
        details: [
          "Cleaning and stain removal from rooms, kitchen, bathroom & balcony",
          "Machine floor scrubbing & dusting of walls & ceiling",
        ],
      },
      {
        title: "Unfurnished apartment",
        price: 3199,
        time: "3 hrs",
        details: [
          "Cleaning and stain removal from rooms, kitchen, bathroom & balcony",
          "Machine floor scrubbing & dusting of walls & ceiling",
        ],
      },
    ],
  },
];

export default class ServiceList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cart: [{ title: "Furnished apartment", quantity: 1 }],
    };

    // Bind methods (important in class components)
    this.addToCart = this.addToCart.bind(this);
    this.buttonFn = this.buttonFn.bind(this);
  }

  addToCart(title) {
    this.setState((prevState) => {
      const cart = [...prevState.cart];
      const index = cart.findIndex((i) => i.title === title);
      if (index >= 0) {
        cart[index].quantity++;
      } else {
        cart.push({ title, quantity: 1 });
      }
      return { cart };
    });
  }

  buttonFn(title) {
    const { cart } = this.state;
    const index = cart.findIndex((i) => i.title === title);
    if (index >= 0) {
      return (
        <button className="overlap-btn" onClick={() => this.addToCart(title)}>
          {cart[index].quantity}
        </button>
      );
    } else {
      return (
        <button className="overlap-btn" onClick={() => this.addToCart(title)}>
          Add
        </button>
      );
    }
  }

  render() {
    return (
      <div className="main-content p-4">
        {services.map((a) => (
          <div className="p-2" key={a.heading}>
            <h3>{a.heading}</h3>
            {a.types.map((b) => (
              <div className="row mt-3" key={b.title}>
                <div className="col-8">
                  <h5>{b.title}</h5>
                  <p>
                    <b>Starts at â‚¹{b.price}</b> &bull; {b.time}
                  </p>
                  <ul>
                    {b.details.map((i, idx) => (
                      <li key={idx}>{i}</li>
                    ))}
                  </ul>
                </div>
                <div className="col-4">
                  <div className="image-wrapper">
                    <img src={apartment} className="img-fluid" alt="service" />
                    {this.buttonFn(b.title)}
                  </div>
                </div>
                <hr className="mt-3" />
              </div>
            ))}
          </div>
        ))}
      </div>
    );
  }
}
