import React, { Component } from "react";
import { withRouter } from "./withRouter";
import "./style.css";

class Checkout extends Component {
  constructor(props) {
    super(props);
    let data = localStorage.getItem("cartItems");
    this.state = {
      cart: data,
    };
  }
  render() {
    return (
      <div className="conatiner row">
        <div className="col-6"></div>
        <div className="col-6">
          <div className="container p-2">
            <h5>Cart</h5>
            {cart.map((a) => (
              <div className="row ">
                <div className="col-7" style={{ fontSize: "14px" }}>
                  {a.title}
                </div>
                <div className="col-2">
                  <button className="merged-btn">
                    <span onClick={() => this.removetocart(a.title)}>–</span>
                    <span className="quantity-text">{a.quantity}</span>
                    <span onClick={() => this.addToCart(a.title, a.price)}>
                      +
                    </span>
                  </button>
                </div>
                <div className="col-3" style={{ fontSize: "14px" }}>
                  ₹{a.price * a.quantity}
                </div>
                <br />
                <br />
              </div>
            ))}
            <hr />
            <div className="row">
              <div className="col-7 text-center">
                <b>Total</b>
              </div>
              <div className="col-2"></div>
              <div className="col-3">
                <b>{cart.reduce((b, c) => b + c.price * c.quantity, 0)}</b>
              </div>
            </div>
            <div className="text-center mt-2">
              <button
                className=" btn checkout-btn text-center"
                onClick={() => this.props.navigate("/checkout")}
              >
                Checkout
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(Checkout);
