import React, { Component } from "react";
import "./style.css";

import apartment from "../images/apartment.jpeg";
import bangalow from "../images/banglow.jpeg";
import homeimage from "../images/room.jpeg";
import miniservices from "../images/miniservice.jpeg";
import homecleaning from "../images/home_cleaning.jpg";

let tick = (
  <svg
    viewBox="0 0 24 24"
    fill="none"
    xmlns="http://www.w3.org/2000/svg"
    style={{ height: "20px" }}
  >
    <path
      fillRule="evenodd"
      clipRule="evenodd"
      d="M9.75 15.836l9.793-9.793 1.414 1.415-10.5 10.5a1 1 0 01-1.414 0l-5.25-5.25 1.414-1.415 4.543 4.543z"
      fill="#0F0F0F"
    ></path>
  </svg>
);

let services = [
  {
    heading: "Apartment",
    types: [
      {
        title: "Furnished apartment",
        price: 3499,
        time: "3 hrs 45 mins",
        details: [
          "Cleaning and stain removal from rooms, kitchen, bathroom & balcony",
          "Machine floor scrubbing & dusting of walls & ceiling",
        ],
      },
      {
        title: "Unfurnished apartment",
        price: 3199,
        time: "3 hrs",
        details: [
          "Cleaning and stain removal from rooms, kitchen, bathroom & balcony",
          "Machine floor scrubbing & dusting of walls & ceiling",
        ],
      },
    ],
  },
  {
    heading: "Bungalow/duplex",
    types: [
      {
        title: "Furnished bungalow",
        price: 4399,
        time: "5 hrs 30 mins",
        details: [
          "Cleaning and stain removal from rooms, kitchen, bathroom, balcony, porch & terrace",
          "Machine floor scrubbing & dusting of walls & ceiling",
        ],
      },
      {
        title: "Unfurnished bungalow",
        price: 3999,
        time: "5 hrs",
        details: [
          "Cleaning and stain removal from rooms, kitchen, bathroom, balcony, porch & terrace",
          "Machine floor scrubbing & dusting of walls & ceiling",
        ],
      },
    ],
  },
];

export default class ServiceList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quantities: {},
      cart: [],
    };
  }

  handleAdd = (key, item, category) => {
    this.setState((prev) => ({
      quantities: { ...prev.quantities, [key]: 1 },
      cart: [
        ...prev.cart,
        {
          category,
          title: item.title,
          price: item.price,
          time: item.time,
          quantity: 1,
        },
      ],
    }));
  };

  handleIncrease = (key) => {
    this.setState((prev) => {
      const updatedCart = prev.cart.map((c) =>
        `${c.category}-${c.title}` === key
          ? { ...c, quantity: c.quantity + 1 }
          : c
      );
      return {
        quantities: { ...prev.quantities, [key]: prev.quantities[key] + 1 },
        cart: updatedCart,
      };
    });
  };

  handleDecrease = (key) => {
    this.setState((prev) => {
      const newQty = Math.max(prev.quantities[key] - 1, 0);
      let updatedCart = prev.cart.map((c) =>
        `${c.category}-${c.title}` === key ? { ...c, quantity: newQty } : c
      );

      // Remove from cart if quantity becomes 0
      if (newQty === 0)
        updatedCart = updatedCart.filter(
          (c) => `${c.category}-${c.title}` !== key
        );

      return {
        quantities: { ...prev.quantities, [key]: newQty },
        cart: updatedCart,
      };
    });
  };

  render() {
    const { quantities, cart } = this.state;

    return (
      <div className="three-column-layout">
        <div className="main-content p-4">
          {services.map((a) => (
            <div className="p-2" key={a.heading}>
              <h3>{a.heading}</h3>

              {a.types.map((b) => {
                const key = `${a.heading}-${b.title}`;
                const count = quantities[key] || 0;

                return (
                  <div className="row mt-3" key={key}>
                    <div className="col-8">
                      <h5>{b.title}</h5>
                      <p>
                        <b>Starts at â‚¹{b.price}</b> &bull; {b.time}
                      </p>
                      <ul>
                        {b.details.map((i, idx) => (
                          <li key={idx}>{i}</li>
                        ))}
                      </ul>
                    </div>

                    <div className="col-4">
                      <div className="image-wrapper">
                        <img
                          src={apartment}
                          alt={b.title}
                          className="img-fluid"
                        />
                        {count === 0 ? (
                          <button
                            className="overlap-btn"
                            onClick={() => this.handleAdd(key, b, a.heading)}
                          >
                            Add
                          </button>
                        ) : (
                          <div className="counter">
                            <button onClick={() => this.handleDecrease(key)}>
                              âˆ’
                            </button>
                            <span>{count}</span>
                            <button onClick={() => this.handleIncrease(key)}>
                              +
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                    <hr />
                  </div>
                );
              })}
            </div>
          ))}
        </div>

        {/* Cart Section */}
        <div className="side right p-4 border">
          <h5>ðŸ›’ Cart ({cart.length})</h5>
          {cart.length === 0 ? (
            <p className="text-secondary">No items in the cart</p>
          ) : (
            <ul>
              {cart.map((item, index) => (
                <li key={index}>
                  <b>{item.title}</b> â€” â‚¹{item.price} Ã— {item.quantity}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    );
  }
}
