import React, { Component } from "react";
import "./style.css";

import apartment from "../images/apartment.jpeg";
import bangalow from "../images/banglow.jpeg";
import homeimage from "../images/room.jpeg";
import miniservices from "../images/miniservice.jpeg";
import homecleaning from "../images/home_cleaning.jpg";

export default class ServiceList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cart: [],
    };
  }

  addToCart = (title, price) => {
    this.setState((prevState) => {
      const cart = [...prevState.cart];
      const index = cart.findIndex((i) => i.title === title);
      if (index >= 0) {
        cart[index].quantity += 1;
      } else {
        cart.push({ title, quantity: 1, price });
      }
      return { cart };
    });
  };

  removetocart = (title) => {
    this.setState((prevState) => {
      const cart = [...prevState.cart];
      const index = cart.findIndex((i) => i.title === title);
      if (index >= 0) {
        if (cart[index].quantity <= 1) {
          cart.splice(index, 1);
        } else {
          cart[index].quantity -= 1;
        }
      }
      return { cart };
    });
  };

  // product button logic (Add / - qty +)
  buttonFn = (title, price) => {
    const { cart } = this.state;
    const index = cart.findIndex((i) => i.title === title);

    if (index >= 0) {
      const quantity = cart[index].quantity;
      return (
        <div className="quantity-container">
          <button className="qty-btn" onClick={() => this.removetocart(title)}>
            –
          </button>
          <span className="quantity-text">{quantity}</span>
          <button
            className="qty-btn"
            onClick={() => this.addToCart(title, price)}
          >
            +
          </button>
        </div>
      );
    } else {
      return (
        <button
          className="overlap-btn"
          onClick={() => this.addToCart(title, price)}
        >
          Add
        </button>
      );
    }
  };

  render() {
    const { cart } = this.state;

    const services = [
      {
        heading: "Apartment",
        types: [
          {
            title: "Furnished apartment",
            price: 3499,
            time: "3 hrs 45 mins",
            details: [
              "Cleaning and stain removal from rooms, kitchen, bathroom & balcony",
              "Machine floor scrubbing & dusting of walls & ceiling",
            ],
          },
          {
            title: "Unfurnished apartment",
            price: 3199,
            time: "3 hrs",
            details: [
              "Cleaning and stain removal from rooms, kitchen, bathroom & balcony",
              "Machine floor scrubbing & dusting of walls & ceiling",
            ],
          },
        ],
      },
      {
        heading: "Mini services",
        types: [
          {
            title: "Kitchen appliances cleaning",
            price: 99,
            time: "30 mins",
            details: ["Deep cleaning of chimney, fridge, microwave, or stove"],
          },
        ],
      },
    ];

    return (
      <div className="three-column-layout">
        {/* Left sidebar */}
        <div className="side left">
          <p className="text-secondary">Select a service</p>
        </div>

        {/* Middle content */}
        <div className="main-content p-4">
          {services.map((section) => (
            <div key={section.heading}>
              <h3>{section.heading}</h3>
              {section.types.map((b) => (
                <div className="row mt-3" key={b.title}>
                  <div className="col-8">
                    <h5>{b.title}</h5>
                    <p>
                      <b>Starts at ₹{b.price}</b> &bull; {b.time}
                    </p>
                    <ul>
                      {b.details.map((i, idx) => (
                        <li key={idx}>{i}</li>
                      ))}
                    </ul>
                  </div>
                  <div className="col-4">
                    <div className="image-wrapper">
                      <img
                        src={apartment}
                        className="img-fluid"
                        alt={b.title}
                      />
                      {this.buttonFn(b.title, b.price)}
                    </div>
                  </div>
                  <hr className="mt-3" />
                </div>
              ))}
            </div>
          ))}
        </div>

        {/* Right sidebar (cart) */}
        <div className="side right">
          <div className="border p-3" style={{ borderRadius: "10px" }}>
            {cart.length > 0 ? (
              <>
                <h5 className="mb-3">Cart</h5>
                {cart.map((item) => (
                  <div className="row align-items-center mb-2" key={item.title}>
                    <div className="col-6">{item.title}</div>
                    <div className="col-4 d-flex justify-content-center gap-2">
                      <button
                        className="qty-btn"
                        onClick={() => this.removetocart(item.title)}
                      >
                        –
                      </button>
                      <span className="quantity-text">{item.quantity}</span>
                      <button
                        className="qty-btn"
                        onClick={() => this.addToCart(item.title, item.price)}
                      >
                        +
                      </button>
                    </div>
                    <div className="col-2 text-end">
                      ₹{item.price * item.quantity}
                    </div>
                  </div>
                ))}
                <hr />
                <h6 className="text-end">
                  Total: ₹
                  {cart.reduce(
                    (total, item) => total + item.price * item.quantity,
                    0
                  )}
                </h6>
              </>
            ) : (
              <div className="text-center">
                <p className="text-secondary mt-3">No items in the cart</p>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }
}
